(function () {
	'use strict';

	var currentPageOpenedFactory = function($rootScope) {
		var pageNotOpened = true;
		var currentPageName = '';
		var nowOpened = function(pageName) {
			switch (currentPageName) {
				case 'page': $rootScope.$broadcast('pageNowClosed'); break;
			}
			var pageOpened = function() {
				$rootScope.$broadcast('pageNowOpened');
				if (pageNotOpened) {
					pageNotOpened = false;
					$rootScope.$broadcast('pageFirstOpened');
				}
			};
			switch (pageName) {
				case 'page': pageOpened(); break;
			}
			currentPageName = pageName;
		};

		var onFirstOpened = function(pageName, callBack) {
			switch (pageName) {
				case 'page': $rootScope.$on('pageFirstOpened', function() { callBack(); }); break;
			}
		};

		var onNowOpened = function(pageName, callBack) {
			switch (pageName) {
				case 'page': $rootScope.$on('pageNowOpened', function() { callBack(); }); break;
			}
		};

		var onNowClosed = function(pageName, callBack) {
			switch (pageName) {
				case 'page': $rootScope.$on('pageNowClosed', function() { callBack(); }); break;
			}
		};

		return { nowOpened: nowOpened, onFirstOpened: onFirstOpened, onNowOpened: onNowOpened, onNowClosed: onNowClosed };
	};
	currentPageOpenedFactory.$inject = ['$rootScope'];
	angular.module('SpamProtester').factory('currentPageOpened', currentPageOpenedFactory);
})();

